class=&#34;crayon-o&#34;&gt;=&lt;/span&gt;&lt;span class=&#34;crayon-s&#34;&gt;&#34;none&#34;&lt;/span&gt;&lt;span class=&#34;crayon-h&#34;&gt; &lt;/span&gt;&lt;span class=&#34;crayon-e&#34;&gt;stroke&lt;/span&gt;&lt;span class=&#34;crayon-o&#34;&gt;=&lt;/span&gt;&lt;span class=&#34;crayon-s&#34;&gt;&#34;red&#34;&lt;/span&gt;&lt;span class=&#34;crayon-h&#34;&gt; &lt;/span&gt;&lt;span class=&#34;crayon-e&#34;&gt;stroke-width&lt;/span&gt;&lt;span class=&#34;crayon-o&#34;&gt;=&lt;/span&gt;&lt;span class=&#34;crayon-s&#34;&gt;&#34;1&#34;&lt;/span&gt;&lt;span class=&#34;crayon-r&#34;&gt;/&amp;gt;&lt;/span&gt;&lt;/div&gt;&lt;div class=&#34;crayon-line&#34; id=&#34;crayon-5fba44a68f352926673531-17&#34;&gt;&lt;span class=&#34;crayon-i&#34;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&#34;crayon-r&#34;&gt;&amp;lt;/pattern&amp;gt;&lt;/span&gt;&lt;/div&gt;&lt;div class=&#34;crayon-line&#34; id=&#34;crayon-5fba44a68f352926673531-18&#34;&gt;&lt;span class=&#34;crayon-i&#34;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&#34;crayon-r&#34;&gt;&amp;lt;/defs&amp;gt;&lt;/span&gt;&lt;/div&gt;&lt;div class=&#34;crayon-line&#34; id=&#34;crayon-5fba44a68f352926673531-19&#34;&gt;&lt;span class=&#34;crayon-i&#34;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&#34;crayon-r&#34;&gt;&amp;lt;rect &lt;/span&gt;&lt;span class=&#34;crayon-e&#34;&gt;width&lt;/span&gt;&lt;span class=&#34;crayon-o&#34;&gt;=&lt;/span&gt;&lt;span class=&#34;crayon-s&#34;&gt;&#34;100%&#34;&lt;/span&gt;&lt;span class=&#34;crayon-h&#34;&gt; &lt;/span&gt;&lt;span class=&#34;crayon-e&#34;&gt;height&lt;/span&gt;&lt;span class=&#34;crayon-o&#34;&gt;=&lt;/span&gt;&lt;span class=&#34;crayon-s&#34;&gt;&#34;100%&#34;&lt;/span&gt;&lt;span class=&#34;crayon-h&#34;&gt; &lt;/span&gt;&lt;span class=&#34;crayon-e&#34;&gt;fill&lt;/span&gt;&lt;span class=&#34;crayon-o&#34;&gt;=&lt;/span&gt;&lt;span class=&#34;crayon-s&#34;&gt;&#34;url(#largeGrid)&#34;&lt;/span&gt;&lt;span class=&#34;crayon-r&#34;&gt; /&amp;gt;&lt;/span&gt;&lt;/div&gt;&lt;div class=&#34;crayon-line&#34; id=&#34;crayon-5fba44a68f352926673531-20&#34;&gt;&lt;span class=&#34;crayon-i&#34;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span class=&#34;crayon-r&#34;&gt;&amp;lt;/svg&amp;gt;&lt;/span&gt;&lt;/div&gt;&lt;/div&gt;&lt;/td&gt;
