&lt;span class=&#34;hljs-comment&#34;&gt;// and will be lost if the code is regenerated&lt;/span&gt;
&lt;span class=&#34;hljs-comment&#34;&gt;//&lt;/span&gt;

&lt;span class=&#34;hljs-keyword&#34;&gt;package&lt;/span&gt; table

&lt;span class=&#34;hljs-keyword&#34;&gt;import&lt;/span&gt; (
	&lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;github.com/go-jet/jet/v2/sqlite&amp;quot;&lt;/span&gt;
)

&lt;span class=&#34;hljs-keyword&#34;&gt;var&lt;/span&gt; Notebook = newNotebookTable(&lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;&amp;quot;&lt;/span&gt;, &lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;notebook&amp;quot;&lt;/span&gt;, &lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;&amp;quot;&lt;/span&gt;)

