	&lt;span class=&#34;hljs-keyword&#34;&gt;return&lt;/span&gt; &amp;amp;NotebookTable&amp;#123;
		notebookTable: newNotebookTableImpl(schemaName, tableName, alias),
		EXCLUDED:      newNotebookTableImpl(&lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;&amp;quot;&lt;/span&gt;, &lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;excluded&amp;quot;&lt;/span&gt;, &lt;span class=&#34;hljs-string&#34;&gt;&amp;quot;&amp;quot;&lt;/span&gt;),
	&amp;#125;
&amp;#125;

&lt;span class=&#34;hljs-function&#34;&gt;&lt;span class=&#34;hljs-keyword&#34;&gt;func&lt;/span&gt; &lt;span class=&#34;hljs-title&#34;&gt;newNotebookTableImpl&lt;/span&gt;&lt;span class=&#34;hljs-params&#34;&gt;(schemaName, tableName, alias &lt;span class=&#34;hljs-type&#34;&gt;string&lt;/span&gt;)&lt;/span&gt;&lt;/span&gt; notebookTable &amp;#123;
	&lt;span class=&#34;hljs-keyword&#34;&gt;var&lt;/span&gt; (
