		mutableColumns = sqlite.ColumnList&amp;#123;TitleColumn&amp;#125;
	)

	&lt;span class=&#34;hljs-keyword&#34;&gt;return&lt;/span&gt; notebookTable&amp;#123;
		Table: sqlite.NewTable(schemaName, tableName, alias, allColumns...),

		&lt;span class=&#34;hljs-comment&#34;&gt;//Columns&lt;/span&gt;
		ID:    IDColumn,
